import React, {useState, useEffect} from 'react'
import { AppBar, Box, IconButton, Toolbar, Drawer, Avatar, Button, useMediaQuery } from '@mui/material'
import { Menu, AccountCircle, Brightness4, Brightness7 } from '@mui/icons-material';
import { Link } from 'react-router-dom';
import { useTheme } from '@mui/material/styles';
import { useDispatch, useSelector } from 'react-redux';
import Search from './Search';
import Sidebar from './Sidebar';
import { fetchToken, createSessionId, moviesApi } from '../utils';
import { setUser, userSelector } from '../features/auth';
import { ColorModeContext } from '../utils/ToggleColorMode';
import { useContext } from 'react';

function NavBar() {
  const {isAuthenticated, user} = useSelector(userSelector)
  const theme = useTheme()
  const dispatch = useDispatch()
  const isMobile = useMediaQuery('(max-width: 600px)')
  const drawerWidth = 240;
  const [mobileOpen, setMobileOpen] = useState(false)
  const colorMode = useContext(ColorModeContext)

  const token = localStorage.getItem('request_token')
  const sessionIdFromLocalStorage = localStorage.getItem('session_id')
  console.log(user)
  
  useEffect(() => {
    const loginUser = async () => {
      if (token) {
        if (sessionIdFromLocalStorage) {
          const { data: userData } = await moviesApi.get(`/account?session_id=${sessionIdFromLocalStorage}`);
          dispatch(setUser(userData));
        } else {
          const sessionId = await createSessionId();
          const { data: userData } = await moviesApi.get(`/account?session_id=${sessionId}`);
          dispatch(setUser(userData));
        }
      }
    }
    loginUser()
  }, [token])

  return (
    <>
      <AppBar position='fixed' >
        <Toolbar sx={{height: '80px', display: 'flex', [theme.breakpoints.down('sm')]: {marginLeft: 0, flexWrap: 'wrap'}, justifyContent: 'space-between', marginLeft: '240px',}} >
          {isMobile && (
            <IconButton color='inherit' edge='start' sx={{outline: 'none', [theme.breakpoints.up('sm')]: {display: 'none',} , marginRight: theme.spacing(2)}} 
              onClick={() => setMobileOpen((prevMobileOpen) => !prevMobileOpen)} >
              <Menu />
            </IconButton>
          )}
          <IconButton color="inherit" onClick={colorMode.toggleColorMode} sx={{ ml: 1 }}>
            {theme.palette.mode === 'dark' ? <Brightness4 /> : <Brightness7 />}
          </IconButton>
          {!isMobile && <Search />}
          <div>
            {!isAuthenticated ? (
              <Button color="inherit" onClick={fetchToken}>
                Login &nbsp; <AccountCircle />
              </Button>
            ) : (
              <Button
                color="inherit"
                component={Link}
                to={`/profile/${user.id}`}
                sx={{'&:hover': {color: 'white !important', textDecoderation: 'none',},}}
                onClick={() => { }}
              >
                {!isMobile && <>My Movies &nbsp;</>}
                <Avatar
                  style={{ width: 30, height: 30 }}
                  alt='profile'
                  src='data:image/png;base64,'
                />
              </Button>
            )}
          </div>
          {isMobile && <Search />}
        </Toolbar>
      </AppBar>
      <Box>
        <Box sx={{[theme.breakpoints.up('sm')]: {width: drawerWidth, flexShrink: 0,}}}>
          {isMobile ? (
            <Drawer 
              variant='temporary' 
              sx={{}}
              onClose={() => setMobileOpen((prevMobileOpen) => !prevMobileOpen)}
              ModalProps={{keepMounted: true}}
              anchor='right'
              classes={{paper: {width: drawerWidth},}} 
              open={mobileOpen}
              >
                <Sidebar setMobileOpen={setMobileOpen} />

            </Drawer>
          ) : (
            <Drawer variant="permanent" open classes={{ paper: {width: drawerWidth}, }}>
              <Sidebar setMobileOpen={setMobileOpen} />
            </Drawer>
          )}
        </Box>
      </Box>
      
    </>
  )
}

export default NavBar